import React, { useContext } from "react";
import classes from "./SelectCar.module.css";
import { AuthContext } from "../../context/auth-context";
import { useHttpClient } from "../../hooks/http-hook";

const SelectCar = ({selectedPay, isOpen, onClose}) => {
      const auth = useContext(AuthContext);
      const { sendRequest } = useHttpClient();
    if(!isOpen){
        return null
    }
const placeSubmitHandler = async () => {
  try {
    await sendRequest(
      `http://localhost:5500/api/payments/${auth.userId}`,
      "POST",
      JSON.stringify({
        id: selectedPay.id,
        name: selectedPay.name,
        zone: selectedPay.zone,
        plate: "X",
        price: selectedPay.price,
      }),
      {
        Authorization: "Bearer " + auth.token,
        "Content-Type": "application/json",
      }
    );

  } catch (err) {}
};
  const payTicket = () => {
      placeSubmitHandler();
  };
  return (
    <div className={classes.car} onClick={onClose}>
      <div className={classes.wrapper}>
        <h4>Lokacija</h4>
        <p>{selectedPay.name}</p>
        <p>{selectedPay.price} KM</p>
        <button onClick={payTicket}>Plati</button>
        <div onClick={onClose}>Odustani</div>
      </div>
    </div>
  );
};

export default SelectCar;
