import React, { useEffect, useState } from "react";
import { GoogleMap, InfoWindowF, MarkerF } from "@react-google-maps/api";
import PayButton from "./PayButton";
import classes from "./Map.module.css";
import { useHttpClient } from "../hooks/http-hook";

function Map() {



const { sendRequest } = useHttpClient();
useEffect(() => {
  const fetchPlaces = async () => {
    try {
      const responseData = await sendRequest(
        `http://localhost:5500/api/places/`,

      );
      setLoadedPlaces(responseData.place);
    } catch (err) {}
  };
  fetchPlaces();
}, [sendRequest]);


  const [activeMarker, setActiveMarker] = useState(null);
  const [currentPosition, setPosition] = useState({
    lat: 0,
    lng: 0,
  });
  const [clickLocation, isClicked] = useState();
  const [zoom, setZoom] = useState(10);
  const [center, setCenter] = useState({
    lat: 0,
    lng: 0,
  });
  const icon = {
    url: "https://maps.google.com/mapfiles/ms/icons/blue-dot.png",
  };

  const handleActiveMarker = (marker) => {
    if (marker === activeMarker) {
      return null;
    }
    setActiveMarker(marker);
  };
  const handleOnLoad = (map) => {
    const bounds = new window.google.maps.LatLngBounds();
    markers.forEach(({ position }) => bounds.extend(position));
    map.fitBounds(bounds);
  };
  const showMyLocation = () => {
    if (currentPosition.lat === 0) {
      alert("Enable location first");
    } else {
      isClicked(
        <MarkerF
          key={currentPosition.lat}
          position={currentPosition}
          options={{
            zIndex: 999,
            icon: icon,
          }}
        ></MarkerF>
      );
      setZoom(15);
      setCenter(currentPosition);
      setActiveMarker(null);
    }
  };

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(function (position) {
      setPosition({
        lat: position.coords.latitude,
        lng: position.coords.longitude,
      });
    });
  }, [currentPosition.lat, currentPosition.lng, zoom, center]);

  return (
    <GoogleMap
      onLoad={handleOnLoad}
      onClick={() => setActiveMarker(null)}
      mapContainerStyle={{ width: "100vw", height: "100vh" }}
      center={center}
      zoom={zoom}
    >
      {markers.map(({ id, name, position, zone }) => (
        <MarkerF
          key={id}
          position={position}
          onClick={() => handleActiveMarker(id)}
        >
          {activeMarker === id && (
            <InfoWindowF onCloseClick={() => setActiveMarker(null)}>
              <div>
                <PayButton name={name} zone={zone} />
              </div>
            </InfoWindowF>
          )}
        </MarkerF>
      ))}
      {!currentPosition.lat !== 0 && (
        <div className={classes.MyLocation} onClick={showMyLocation}>
          Find my location
        </div>
      )}
      {clickLocation}
    </GoogleMap>
  );
}

export default Map;
