import React, { useContext, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useHttpClient } from "../../hooks/http-hook";
import { AuthContext } from "../../context/auth-context";
import classes from "./LastPayments.module.css";

const LastPayments = () => {
  const [isSelectCarOpen, setIsSelectCarOpen] = useState(false);
  const [selectedPay, setSelectedPay] = useState([]);
  const [loadedCars, setLoadedCars] = useState([]);
  const { sendRequest } = useHttpClient();
  const auth = useContext(AuthContext);
  const [loadedPayments, setLoadedPayments] = useState([]);

    const [body, isBody] = useState("Unesite Vaše registarske oznake");
    const [numH, isNumHourly] = useState(12);
    const [numD, isNumDaily] = useState(15);


    useEffect(() => {
      isBody(selectedPay.plate);
      if (selectedPay.zone === 0) {
        isNumHourly(10);
        isNumDaily(13);
      }
      if (selectedPay.zone === 1) {
        isNumHourly(11);
        isNumDaily(14);
      }
      if (selectedPay.zone === 2) {
        isNumHourly(12);
        isNumDaily(15);
      }
    }, [selectedPay.zone, selectedPay.plate]);


  useEffect(() => {
    const fetchPayments = async () => {
      try {
        const responseData = await sendRequest(
          `http://localhost:5500/api/payments/${auth.userId}/last`,
          "GET",
          null,
          {
            Authorization: "Bearer " + auth.token,
          }
        );
        setLoadedPayments(responseData.payments);
      } catch (err) {}
    };
    fetchPayments();
  }, [sendRequest, auth.token, auth.userId]);

  useEffect(() => {
    const fetchCars = async () => {
      try {
        const responseData = await sendRequest(
          `http://localhost:5500/api/cars/user/${auth.userId}`,
          "GET",
          null,
          {
            Authorization: "Bearer " + auth.token,
          }
        );
        setLoadedCars(responseData.cars);
      } catch (err) {}
    };
    fetchCars();
    // eslint-disable-next-line
  }, [sendRequest,loadedCars]);

  const carPayment = async (plate) => {
    try {
      await sendRequest(
        `http://localhost:5500/api/payments/${auth.userId}`,
        "POST",
        JSON.stringify({
          id: selectedPay.id,
          name: selectedPay.name,
          zone: selectedPay.zone,
          plate: plate,
          price: selectedPay.price,
        }),
        {
          Authorization: "Bearer " + auth.token,
          "Content-Type": "application/json",
        }
      );
    } catch (err) {
      console.log(err);
    }
  };

  const carSelect = (payment) => {
    setSelectedPay(payment);
    setIsSelectCarOpen(true);
  };

  const closeSelectCar = () => {
    setIsSelectCarOpen(false);
  };
  const carPay = (plate) => {
    carPayment(plate);
  };
  return (
    <React.Fragment>
      <h5 className={classes.text}>Klikni da platiš ponovo</h5>
      {loadedPayments.map((payment) => (
        <Link key={payment._id} onClick={() => carSelect(payment)}>
          {payment.name}
        </Link>
      ))}
      {isSelectCarOpen && (
        <div className={classes.car} onClick={closeSelectCar}>
          <div className={classes.wrapper}>
            <h4 className={classes.title}>Lokacija</h4>
            <p>{selectedPay.name}</p>
            <p>{selectedPay.price} KM</p>
            <h4 className={classes.title}>Izaberite vozilo:</h4>
            {loadedCars.map((cars) => (
              <button
                key={cars._id}
                className={classes.plates}
                onClick={() => carPay(cars.plate)}
              >
                <Link className={classes.link} to={"sms:+3878335;?&body="}>
                  {cars.name} - ({cars.plate})
                </Link>
              </button>
            ))}
            <div onClick={closeSelectCar}>Odustani</div>
          </div>
        </div>
      )}
    </React.Fragment>
  );
};

export default LastPayments;
