import React, { useContext } from 'react'

import { useHttpClient } from '../../hooks/http-hook';
import { AuthContext } from '../../context/auth-context';
import { useForm } from '../../hooks/form-hook';
import { VALIDATOR_REQUIRE } from '../../util/validators';

import Button from '../components/FormElements/Button';
import Input from '../components/FormElements/Input';
import LoadingSpinner from '../components/LoadingSpinner';

import classes from ""

const UserAccount = () => {
      const auth = useContext(AuthContext);
      const { isLoading, sendRequest } = useHttpClient();
        const [formState, inputHandler] = useForm(
          {
            email: {
              value: "",
              isValid: false,
            },
            password: {
              value: "",
              isValid: false,
            },
          },
          false
        );

      const userDataString = localStorage.getItem("userData");
      const userDataObject = JSON.parse(userDataString);
      const userId = userDataObject.userId;


      const updateUser = async (event) => {
        event.preventDefault();
        try {
          await sendRequest(
            `http://localhost:5500/api/users/${userId}`,
            "PATCH",
            JSON.stringify({
              email: formState.inputs.email.value,
              password: formState.inputs.password.value,
            }),
            {
              Authorization: "Bearer " + auth.token,
              "Content-Type": "application/json",
            }
          );
        } catch (err) {}
      };
  return (
    <div className="main">
      <p className="title">Va≈° Parking</p>
      <h3 className="heading">Spisak vozila</h3>
      <div className={classes.options + " options"}>
        <div className={classes.addingCars}>
          <h4> Dodaj vozilo</h4>
          <form className={classes.wrapper} onSubmit={updateUser}>
            <Input
              id="name"
              element="input"
              type="text"
              label="Naziv vozila"
              validators={[VALIDATOR_REQUIRE()]}
              errorText="Unesite naziv vozila"
              onInput={inputHandler}
            />

            <Input
              id="plate"
              element="input"
              label="Registarske oznake"
              validators={[VALIDATOR_REQUIRE()]}
              errorText="Unesite registarske oznake"
              onInput={inputHandler}
            />
            <Button type="submit" disabled={!formState.isValid}>
              ADD PLACE
            </Button>
          </form>
        </div>
      </div>
      {isLoading && <LoadingSpinner />}
    </div>
  );
}

export default UserAccount
