    placeSubmitHandler();
    console.log(selectedPay);
  };
  return (
    <div className={classes.car} onClick={onClose}>
      <div className={classes.wrapper}>
        <h4>Lokacija</h4>
        <p>{selectedPay.name}</p>
        <p>{selectedPay.price} KM</p>
        <button onClick={payTicket}>Plati</button>
        <div onClick={onClose}>Odustani</div>
      </div>
    </div>
  );
};

export default SelectCar;
