import React, { useState, useContext, useEffect } from "react";
import { useHttpClient } from "../../hooks/http-hook";
import { AuthContext } from "../../context/auth-context";
import classes from "./LastPayments.module.css";
import { Link } from "react-router-dom";

const CarSelection = ({ selectedPay, onClose }) => {
    const[selectedPays, setSelectedPay] = useState([])
  const auth = useContext(AuthContext);
  const { sendRequest } = useHttpClient();
  const [loadedCars, setLoadedCars] = useState([]);
  const [body, isBody] = useState("Unesite Vaše registarske oznake");
  const [numH, isNumHourly] = useState(12);
  const [numD, isNumDaily] = useState(15);

  useEffect(() => {
    if (selectedPay.plate) {
      isBody(selectedPay.plate);
    }
    if (selectedPay.zone === 0) {
      isNumHourly(10);
      isNumDaily(13);
    }
    if (selectedPay.zone === 1) {
      isNumHourly(11);
      isNumDaily(14);
    }
    if (selectedPay.zone === 2) {
      isNumHourly(12);
      isNumDaily(15);
    }
  }, [selectedPay.zone, selectedPay.plate]);

  useEffect(() => {
    setSelectedPay(selectedPay)
    const fetchCars = async () => {
      try {
        const responseData = await sendRequest(
          `http://localhost:5500/api/cars/user/${auth.userId}`,
          "GET",
          null,
          {
            Authorization: "Bearer " + auth.token,
          }
        );
        setLoadedCars(responseData.cars);
      } catch (err) {}
    };
    fetchCars();
  }, []);
  useEffect(() => {
    const fetchCars = async () => {
      try {
        const responseData = await sendRequest(
          `http://localhost:5500/api/cars/user/${auth.userId}`,
          "GET",
          null,
          {
            Authorization: "Bearer " + auth.token,
          }
        );
        setLoadedCars(responseData.cars);
      } catch (err) {}
    };
    fetchCars();
    // eslint-disable-next-line
  }, [sendRequest, loadedCars]);
  const carPayment = async (plate) => {
    try {
      await sendRequest(
        `http://localhost:5500/api/payments/${auth.userId}`,
        "POST",
        JSON.stringify({
          id: selectedPay.id,
          name: selectedPay.name,
          zone: selectedPay.zone,
          plate: plate,
          price: selectedPay.price,
        }),
        {
          Authorization: "Bearer " + auth.token,
          "Content-Type": "application/json",
        }
      );
      onClose(); // Close the car selection after payment
    } catch (err) {
      console.log(err);
    }
  };
  const carPay = (plate) => {
    carPayment(plate);
  };
  return (
    <div>
      {loadedCars.map((cars) => (
        <button
          key={cars._id}
          className={classes.plates}
          onClick={() => carPay(cars.plate)}
        >
          <Link
            className={classes.link}
            to={"sms:+3878335" + numH + ";?&body=" + cars.plate}
          >
            {cars.name} - ({cars.plate})
          </Link>
        </button>
      ))}
      <div onClick={onClose}>Odustani</div>
    </div>
  );
};

export default CarSelection;
