import React, { useContext, useEffect, useState } from "react";
import { useHttpClient } from "../../hooks/http-hook";
import classes from "./UserName.module.css";
import { AuthContext } from "../../context/auth-context";

const UserName = () => {
  const { sendRequest } = useHttpClient();
  const [loadedName, setLoadedName] = useState();

  const userDataString = localStorage.getItem("userData");
  const userDataObject = JSON.parse(userDataString);
  const userId = userDataObject.userId;
  const auth = useContext(AuthContext);
  useEffect(() => {
    const fetchName = async () => {
      try {
        const responseData = await sendRequest(
          `http://localhost:5500/api/users/${userId}`,
        );
        setLoadedName(responseData.user.name);
      } catch (err) {}
    };
    fetchName();
  }, [sendRequest, loadedName, userId, auth.token]);
  return <p className={classes.userName}>Dobar dan! {loadedName}</p>;
};

export default UserName;
