import React, { useContext, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useHttpClient } from "../../hooks/http-hook";
import { AuthContext } from "../../context/auth-context";
import classes from "./LastPayments.module.css";

const LastPayments = () => {
  const [isSelectCarOpen, setIsSelectCarOpen] = useState(false);
  const [selectedPay, setSelectedPay] = useState([]);
   const [loadedCars, setLoadedCars] = useState([]);
  const { sendRequest } = useHttpClient();
  const auth = useContext(AuthContext);

  const [loadedPayments, setLoadedPayments] = useState([]);

  useEffect(() => {
    const fetchPayments = async () => {
      try {
        const responseData = await sendRequest(
          `http://localhost:5500/api/payments/${auth.userId}/last`,
          "GET",
          null,
          {
            Authorization: "Bearer " + auth.token,
          }
        );
        setLoadedPayments(responseData.payments);
      } catch (err) {}
    };
    fetchPayments();
  }, [sendRequest, auth.token, auth.userId]);

    useEffect(() => {
      const fetchCars = async () => {
        try {
          const responseData = await sendRequest(
            `http://localhost:5500/api/cars/user/${auth.userId}`,
            "GET",
            null,
            {
              Authorization: "Bearer " + auth.token,
            }
          );
          setLoadedCars(responseData.cars);
        } catch (err) {}
      };
      fetchCars();
      // eslint-disable-next-line
    }, [sendRequest, auth.userId]);

  const carPayment = async () => {
    try {
      await sendRequest(
        `http://localhost:5500/api/payments/${auth.userId}`,
        "POST",
        JSON.stringify({
          id: selectedPay.id,
          name: selectedPay.name,
          zone: selectedPay.zone,
          plate: selectedPay.plate,
          price: selectedPay.price,
        }),
        {
          Authorization: "Bearer " + auth.token,
          "Content-Type": "application/json",
        }
      );
    } catch (err) {
      console.log(err);
    }
  };

  const carSelect = (payment) => {
    setSelectedPay(payment);
    setIsSelectCarOpen(true);
  };

  const closeSelectCar = () => {
    setIsSelectCarOpen(false);
  };
  const carPay = () => {
    carPayment();
  };
  return (
    <React.Fragment>
      <h5 className={classes.text}>Klikni da plati≈° ponovo</h5>
      {loadedPayments.map((payment) => (
        <Link key={payment._id} onClick={() => carSelect(payment)}>
          {payment.name}
        </Link>
      ))}
      {isSelectCarOpen && (
        <div className={classes.car} onClick={closeSelectCar}>
          <div className={classes.wrapper}>
            <h4>Lokacija</h4>
            <p>{selectedPay.name}</p>
            <p>{selectedPay.price} KM</p>
            {loadedCars.map((cars) => (
              <div key={cars._id} className={classes.plates}>
                <button className={classes.car}>
                  {cars.name}
                  <br></br> {cars.plate}
                </button>
                <button
                  className={classes.delete}
                  onClick={() => handleDelete(cars._id)} // Attach the delete function to the button
                >
                  X
                </button>
              </div>
            ))}
            <button onClick={carPay}>Plati</button>
            <div onClick={closeSelectCar}>Odustani</div>
          </div>
        </div>
      )}
    </React.Fragment>
  );
};

export default LastPayments;
