import React, { useContext, useEffect, useState } from "react";

import { useHttpClient } from "../../hooks/http-hook";
import LoadingSpinner from "../components/LoadingSpinner";

import classes from "./UserCars.module.css";
import { AuthContext } from "../../context/auth-context";
import Input from "../components/FormElements/Input";
import { VALIDATOR_REQUIRE } from "../../util/validators";
import Button from "../components/FormElements/Button";
import { useForm } from "../../hooks/form-hook";

const UserCars = () => {
  const auth = useContext(AuthContext);
  const [popup, setPopup] = useState(false);
  const [loadedCars, setLoadedCars] = useState([]);
  const { isLoading, sendRequest } = useHttpClient();
  const [newCarData, setNewCarData] = useState({
    name: "",
    plate: "",
  });
    const [formState, inputHandler] = useForm(
      {
        title: {
          value: "",
          isValid: false,
        },
        description: {
          value: "",
          isValid: false,
        },
        address: {
          value: "",
          isValid: false,
        },
        image: {
          value: null,
          isValid: false,
        },
      },
      false
    );

  const userDataString = localStorage.getItem("userData");
  const userDataObject = JSON.parse(userDataString);
  const userId = userDataObject.userId;

  useEffect(() => {
    console.log(newCarData)
    const fetchPlaces = async () => {
      try {
        const responseData = await sendRequest(
          `http://localhost:5500/api/cars/user/${userId}`
        );
        setLoadedCars(responseData.cars);
      } catch (err) {}
    };
    fetchPlaces();
    console.log(loadedCars);

  }, [sendRequest, userId]);

  const handleDelete = async (carId) => {
    try {
      await sendRequest(
        `http://localhost:5500/api/cars/${carId}`,
        "DELETE",
        null,
        {
          Authorization: "Bearer " + auth.token,
        }
      );
      // Update state by filtering out the deleted car
      setLoadedCars((prevCars) => prevCars.filter((car) => car._id !== carId));
    } catch (err) {
      // Handle error
    }
  };

  const handleAddCar = async () => {
    try {
      const responseData = await sendRequest(
        `http://localhost:5500/api/cars/${userId}`,
        "POST",
        JSON.stringify(newCarData),
        {
          Authorization: "Bearer " + auth.token,
          "Content-Type": "application/json",
        }
      );

      // Update state with the newly added car
      setLoadedCars((prevCars) => [...prevCars, responseData.cars]);

      // Clear the form inputs
      setNewCarData({
        name: "",
        plate: "",
      });
      setPopup(false);
    } catch (err) {
      // Handle error
    }
  };
  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setNewCarData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };




   const placeSubmitHandler = async (event) => {
     event.preventDefault();
     try {
       const formData = new FormData();
       formData.append("title", formState.inputs.title.value);
       formData.append("description", formState.inputs.description.value);
       formData.append("address", formState.inputs.address.value);
       formData.append("image", formState.inputs.image.value);
       await sendRequest(
         process.env.REACT_APP_BACKEND_URL + "/places",
         "POST",
         formData,
         {
           Authorization: "Bearer " + auth.token,
         }
       );
       navigate("/");
     } catch (err) {}
   };
  return (
    <React.Fragment>
      <div className="main">
        <p className="title">Vaš Parking</p>
        <h3 className="heading">Spisak vozila</h3>
        <div className="options">
          {loadedCars.map((cars) => (
            <div key={cars._id} className={classes.plates}>
              <button className={classes.car}>
                {cars.name}
                <br></br> {cars.plate}
              </button>
              <button
                className={classes.delete}
                onClick={() => handleDelete(cars._id)} // Attach the delete function to the button
              >
                X
              </button>
            </div>
          ))}
          <button className={classes.add} onClick={() => setPopup(true)}>
            Dodaj
          </button>
        </div>
        {isLoading && <LoadingSpinner />}
      </div>
      {popup && (
        <div className={classes.addingCars}>
          <div className={classes.wrapper}>
            <h4>Dodaj vozilo</h4>
            <input
              type="text"
              name="name"
              placeholder="Naziv vozila"
              value={newCarData.name}
              onChange={handleInputChange}
              required={true}
            />
            <input
              type="text"
              name="plate"
              placeholder="Registarske oznake"
              value={newCarData.plate}
              onChange={handleInputChange}
              required={true}
            />
            <button className={classes.add} onClick={handleAddCar}>
              Dodaj
            </button>
            <button className={classes.cancle} onClick={() => setPopup(false)}>
              Odustani
            </button>
          </div>
        </div>
      )}
      <form className={classes["place-form"]} onSubmit={placeSubmitHandler}>
        {isLoading && <LoadingSpinner asOverlay />}
        <Input
          id="name"
          element="input"
          type="text"
          label="Car name"
          validators={[VALIDATOR_REQUIRE()]}
          errorText="Please enter a valid title."
          onInput={inputHandler}
        />

        <Input
          id="address"
          element="input"
          label="Address"
          validators={[VALIDATOR_REQUIRE()]}
          errorText="Please enter a valid address."
          onInput={inputHandler}
        />
        <Button type="submit" disabled={!formState.isValid}>
          ADD PLACE
        </Button>
      </form>
    </React.Fragment>
  );
};

export default UserCars;
