import React, { useContext, useEffect, useState } from "react";
import classes from "./SelectCar.module.css";
import { AuthContext } from "../../context/auth-context";
import { useHttpClient } from "../../hooks/http-hook";

const SelectCar = ({ selectedPay, isOpen, onClose }) => {
    constÂ [carData, setCarData] = useState([])
  const auth = useContext(AuthContext);
  const { sendRequest } = useHttpClient();

  if (!isOpen) {
    return null;
  }  
  const placeSubmitHandler = async () => {
    setCarData(selectedPay)
    try {
      const responseData = await sendRequest(
        `http://localhost:5500/api/payments/${auth.userId}`,
        "POST",
        carData,
        {
          Authorization: "Bearer " + auth.token,
          "Content-Type": "application/json",
        }
      );
      console.log(responseData);
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className={classes.car} onClick={onClose}>
      <div className={classes.wrapper}>
        <h4>Lokacija</h4>
        <p>{selectedPay.name}</p>
        <p>{selectedPay.price} KM</p>
        <button onClick={placeSubmitHandler}>Plati</button>
        <div onClick={onClose}>Odustani</div>
      </div>
    </div>
  );
};

export default SelectCar;
