import React, { useEffect, useState } from "react";
import { useHttpClient } from "../../hooks/http-hook";
import classes from "./ListLocations.module.css";

const ListLocations = () => {
  const [pageNumber, setPageNumber] = useState(0);
  const [loadedPlaces, setLoadedPlaces] = useState();
  const [totalPages, setTotalPages] = useState();
  const { sendRequest } = useHttpClient();
  const pages = new Array(totalPages).fill(null).map((v, i) => i);
  useEffect(() => {
    const fetchPlaces = async () => {
      try {
        const responseData = await sendRequest(
          `http://localhost:5500/api/places/?page=${pageNumber}`
        );
        setLoadedPlaces(responseData.place);
        setTotalPages(responseData.totalPages);
      } catch (err) {}
    };
    fetchPlaces();
  }, [pageNumber]);
  const goPrevious = () => {
    setPageNumber(Math.max(0, pageNumber - 1));
  };
  const goNext = () => {
    setPageNumber(Math.min(totalPages - 1, pageNumber + 1));
    const componentStyle = {}
  };
  return (
    <div className="main">
      <p className="title">Va≈° Parking</p>
      <h3 className="heading">Spisak lokacija</h3>
      <div className={classes.options + " options"}>
        {loadedPlaces?.map((placesNew) => (
          <a key={placesNew.name}>{placesNew.name}</a>
        ))}
      </div>
      <div className={classes.pagination}>
        <button onClick={goPrevious}> &lt; </button>
        {pages.map((pageIndex) => (
          <button style={pageIndex===pageNumber ? {color: "var(--text-primary)"} : '' } key={pageIndex} onClick={() => setPageNumber(pageIndex)}>
            {pageIndex + 1}
          </button>
        ))}
        <button onClick={goNext}> &gt; </button>
      </div>
    </div>
  );
};

export default ListLocations;
