import React, { useContext, useEffect, useState } from "react";

import { useHttpClient } from "../../hooks/http-hook";
import LoadingSpinner from "../components/LoadingSpinner";

import classes from "./UserCars.module.css";
import { AuthContext } from "../../context/auth-context";

const UserCars = () => {
      const auth = useContext(AuthContext);
  const [loadedCars, setLoadedCars] = useState([]);
  const { isLoading, sendRequest } = useHttpClient();

  const userDataString = localStorage.getItem("userData");
  const userDataObject = JSON.parse(userDataString);
  const userId = userDataObject.userId;

  useEffect(() => {
    const fetchPlaces = async () => {
      try {
        const responseData = await sendRequest(
          `http://localhost:5500/api/cars/user/${userId}`
        );
        setLoadedCars(responseData.cars);
      } catch (err) {}
    };
    fetchPlaces();

  }, [sendRequest, userId]);

const handleDelete = async (carId) => {
  try {
    await sendRequest(
      `http://localhost:5500/api/cars/${carId}`,
      "DELETE",
      null,
      {
        Authorization: "Bearer " + auth.token,
      }
    );

    // Update state by filtering out the deleted car
    setLoadedCars((prevCars) => prevCars.filter((car) => car._id !== carId));
  } catch (err) {
    // Handle error
  }
};
  return (
    <div className="main">
      <p className="title">Va≈° Parking</p>
      <h3 className="heading">Spisak vozila</h3>
      <div className="options">
        {loadedCars.map((cars) => (
          <div className={classes.plates}>
            <button className={classes.car} key={cars._id}>
              {cars.name}
              <br></br> {cars.plate}
            </button>
            <button
              className={classes.delete}
              onClick={() => handleDelete(cars._id)} // Attach the delete function to the button
            >
              X
            </button>
          </div>
        ))}
        <button className={classes.add}>Dodaj</button>
      </div>
      {isLoading && <LoadingSpinner />}

      <div className="addingCars">
        <h1>Dodaj vozilo</h1>
        <form>
            <label placeholder="Unesi naziv">Unesi naziv</label>
            <input placeholder="Unes"></input>        </form>
      </div>
    </div>
  );
};

export default UserCars;
